import React, { Component } from 'react';
// import { Link } from 'react-router-dom';
import { Image, Button, Row, Container, Col } from 'react-bootstrap';
import './main.css';
import './home.css';

// import { HOME } from '../strings'

export default class Home extends Component {
    constructor() {
        super();
        this.state = {
            width: window.innerWidth,
        };
    }

    componentWillMount() {
        window.addEventListener('resize', this.handleWindowSizeChange);
    }

    componentWillUnmount() {
        window.removeEventListener('resize', this.handleWindowSizeChange);
    }

    handleWindowSizeChange = () => {
        this.setState({ width: window.innerWidth });
      };

    render() {
        document.title = 'Home - Joseph';
        // const { width } = this.state;
        // const isMobile = width <= 500;
        // const colClass = (isMobile) ? 'home-col-centered' : 'home-col'

        return (
            <Container style={{ height: '100vh', display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                <Row className='align-items-center justify-content-space-evenly'>
                    {/* <Image src="assets/website_photo.jpg" roundedCircle className="profile-pic" /> */}
                    <Col md lg='4' className='home-col'>
                        {/* <Card className="borderless" style={{ maxHeight: '400px', paddingBottom: '5px' }}> */}
                            {/* <h2 className='bio-description header'>{HOME.header}</h2> */}
                            {/* <p className='bio-description body'>{HOME.desc}</p> */}
                        {/* </Card> */}
                        <Button className='homeBtn' href='/experience' style={{ height: '50px', width: '200px' }}>
                            My work >
                        </Button>
                    </Col>
                </Row>
            </Container>
        )
    }
}
