import React, { Component } from 'react';
// import { Link } from 'react-router-dom';
import { Image, Button, Row, Container, Col } from 'react-bootstrap';
import './main.css';
import './home.css';

import Timer from './Components/Timer';

export default class About extends Component {
    constructor() {
        super();
        this.state = {
            width: window.innerWidth,
        };
    }

    componentWillMount() {
        window.addEventListener('resize', this.handleWindowSizeChange);
    }

    componentWillUnmount() {
        window.removeEventListener('resize', this.handleWindowSizeChange);
    }

    handleWindowSizeChange = () => {
        this.setState({ width: window.innerWidth });
      };

    render() {
        document.title = 'About Día de Dilla';
        // const { width } = this.state;
        // const isMobile = width <= 500;
        // const colClass = (isMobile) ? 'home-col-centered' : 'home-col'

        return (
            <Container style={{ height: '100vh', display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                <Row className='align-items-center justify-content-space-evenly'>
                    <Timer timeTillDate='04 25 2020, 7:00 pm' timeFormat='MM DD YYY, h:mm a' />
                </Row>
            </Container>
        )
    }
}
