import React, { Component } from 'react';
import { Navbar, Nav, NavItem } from 'react-bootstrap';
import classnames from "classnames";
import { Link } from 'react-router-dom';

import NavBarItem from './NavBarItem';

import './nav.css';
import './main.css';

export default class Navigation extends Component {
    constructor(props) {
        super(props);

        this.state = {
            prevScrollpos: window.pageYOffset,
            visible: true,
            selected: ''
        }
    }

    componentDidMount() {
        window.addEventListener('scroll', this.handleScroll);
    }

    componentWillUnmount() {
        window.removeEventListener('scroll', this.handleScroll);
    }

    handleScroll = () => {
        // const { prevScrollpos } = this.state;

        const currentScrollPos = window.pageYOffset;
        // const visible = prevScrollpos > currentScrollPos;
        const visible = currentScrollPos === 0;

        this.setState({
            prevScrollpos: currentScrollPos,
            visible
        });
    };

    render() {
        return (
            <Navbar collapseOnSelect expand='sm' fixed='top' id='navbar' className={classnames('navbar', {
                'navbar--scrolled': !this.state.visible,
                'navbar--unscrolled': this.state.visible
            })}>
                <Navbar.Brand href='/' style={{paddingLeft: '5vw'}}>
                    <img 
                        alt=''
                        src='assets/logo.png'
                        style={{
                            'max-height': '50%',
                            'width': 'auto'
                        }}
                        className='d-inline-block align-center'
                    />
                </Navbar.Brand>
                {/* <Navbar.Toggle aria-controls='responsive-navbar-nav' /> */}
                <Navbar.Collapse id='responsive-navbar-nav'>
                    <Nav className='ml-auto' style={{paddingRight: '50px'}} pullRight>
                        <NavBarItem id='home' href='/home' onClick={() => {this.setState({ selected: 'home'});}} label='Home' selected={this.state.selected} />
                        <NavBarItem id='about' href='/about' onClick={() => {this.setState({ selected: 'about'});}} label='About' selected={this.state.selected} />
                    </Nav>
                </Navbar.Collapse>
            </Navbar>
        )
    }
}
